import { takeEvery, call, put } from "redux-saga/effects";
import { FETCH_PROFILES } from "./constants.saga"
import profilesService from "./profiles.service";
import { setProducts } from "../actions/products.actions";

function* fetchProfiles() {
  try {
    const products = yield call(profilesService.getAllProfiles);

    yield put(setProducts(profiles));
  } catch (e) {}
}

export function* waitForFetchProfiles() {
  yield takeEvery(FETCH_PROFILES, fetchProfiles);
}
